import {BaseUniversalTableComponent} from "../common/baseUniversalTableComponent";
import {IUniversalTableViewHeader} from "../../services/universalTable/common/interfaces/IUniversalTableViewHeader";
import {UniversalTableCtrl} from "../../services/universalTable/universalTableCtrl";
import {UniversalTableHeaderData} from "../../directives/universalTableHeaders/universalTableHeaders.directive";

class BaseUniversalTableHeadersComponent extends BaseUniversalTableComponent{
    protected headerIndex:number;
    protected headerData: IUniversalTableViewHeader;

    constructor(tableCtrl: UniversalTableCtrl, headerData: UniversalTableHeaderData){
        super(tableCtrl);
        this.headerData = headerData.headerData;
        this.headerIndex = headerData.headerIndex;
    }
}

export {BaseUniversalTableHeadersComponent}