import {Directive, ComponentFactoryResolver, ViewContainerRef} from "@angular/core";
import {UniversalTableCtrl} from "../../services/universalTable/universalTableCtrl";
import {BaseUniversalTableGridDirective} from "./BaseUniversalTableGridDirective";
import {baseUniversalTableGridDirectiveConfig} from "./baseUniversalTableGridDirectiveConfig.const";

@Directive(baseUniversalTableGridDirectiveConfig)
class UniversalTableGridDirective extends BaseUniversalTableGridDirective{
    constructor(
        componentResolver: ComponentFactoryResolver,
        viewContainer: ViewContainerRef,
        tableCtrl: UniversalTableCtrl
    ){
        super(
            tableCtrl,
            componentResolver,
            viewContainer
        );
    }
}

export {UniversalTableGridDirective, baseUniversalTableGridDirectiveConfig, BaseUniversalTableGridDirective}