import {BaseUniversalTableDirective} from "../common/baseUniversalTableDirective";
import {UniversalTableCtrl} from "../../services/universalTable/universalTableCtrl";
import {ComponentFactoryResolver, ViewContainerRef} from "@angular/core";

abstract class BaseUniversalTablePaginatorDirective extends BaseUniversalTableDirective{
    constructor(
        tableCtrl: UniversalTableCtrl,
        componentResolver: ComponentFactoryResolver,
        viewContainer: ViewContainerRef
    ){
        super(
            componentResolver,
            viewContainer,
            tableCtrl
        );
    }

    ngOnInit(){
        this.initTablePart('paginator');
    }
}

export {BaseUniversalTablePaginatorDirective}