import {Directive, ComponentFactoryResolver, ViewContainerRef} from "@angular/core";
import {UniversalTableCtrl} from "../../services/universalTable/universalTableCtrl";
import {BaseUniversalTablePaginatorDirective} from "./BaseUniversalTablePaginatorDirective";
import {baseUniversalTablePaginatorDirectiveConfig} from "./baseUniversalTablePaginatorDirectiveConfig.const";

@Directive(baseUniversalTablePaginatorDirectiveConfig)
class UniversalTablePaginatorDirective extends BaseUniversalTablePaginatorDirective{
    constructor(
        componentResolver: ComponentFactoryResolver,
        viewContainer: ViewContainerRef,
        tableCtrl: UniversalTableCtrl
    ){
        super(
            tableCtrl,
            componentResolver,
            viewContainer
        );
    }
}

export {UniversalTablePaginatorDirective, BaseUniversalTablePaginatorDirective, baseUniversalTablePaginatorDirectiveConfig}