import {UniversalTableCtrl} from "../universalTableCtrl";
import {IUniversalTableCtrlInitParams} from "../common/interfaces/IUniversalTableCtrlInitParams";
import {UniversalTableConfigurator} from "../baseUniversalTableConfigurator/baseUniversalTableConfigurator";

class BaseUniversalTableService{
    constructor(
        public config: UniversalTableConfigurator
    ){}

    public createInstance(initParams: IUniversalTableCtrlInitParams){
        return new UniversalTableCtrl(
            initParams,
            this.config
        )
    }
}

export {BaseUniversalTableService}