import {UniversalTableCtrl} from './universalTableCtrl'
import {UniversalTableConfigurator} from "./baseUniversalTableConfigurator/baseUniversalTableConfigurator";

describe('test case for universal table ctrl', ()=>{
    it('create new instance don`t throw error', ()=>{
        let configurator = new UniversalTableConfigurator();

        expect(()=>{
            let instance = new UniversalTableCtrl({
                getDataMethod: ()=>{
                    return <any>'get data method call'
                }},
                configurator
            )
        }).not.toThrow()
    })
});