import {BaseIndexedDBRepository} from './indexedDbRepository'

describe('tests for indexed DB repository', ()=>{
    describe('test support indexedDB', ()=>{
        it('create new DB', ()=>{
            let testBd: BaseIndexedDBRepository;

            if (window.hasOwnProperty('indexedDB')){
                expect(()=>{
                    testBd = new BaseIndexedDBRepository('test', 1)
                }).not.toThrowError();
            }else{
                expect(()=>{
                    testBd = new BaseIndexedDBRepository('test', 1)
                }).toThrowError('indexedDB not support');
            }
        })
    });
});