import {isNull} from './isNull'

describe('test for is null function', ()=>{
    it('test null variable', ()=>{
        let value: string = null;

        expect(isNull(value)).toBeTruthy()
    });

    it('test null property', ()=>{
        let data: any = {
            test: null
        };

        expect(isNull(data.test)).toBeTruthy()
    })
})