import {isUndefined} from './isUndefined'

describe('test for is null function', ()=>{
    it('test null variable', ()=>{
        let value: string;

        expect(isUndefined(value)).toBeTruthy()
    });

    it('test null property', ()=>{
        let data: any = {};

        expect(isUndefined(data.test)).toBeTruthy()
    })
})