"use strict";
require("core-js");
require("zone.js/dist/zone");
require("zone.js/dist/long-stack-trace-zone");
require("zone.js/dist/proxy");
require("zone.js/dist/sync-test");
require("zone.js/dist/jasmine-patch");
require("zone.js/dist/async-test");
require("zone.js/dist/fake-async-test");
var platform_browser_1 = require("@angular/platform-browser");
var testing_1 = require("@angular/core/testing");
var testing_2 = require("@angular/platform-browser-dynamic/testing");
var angular2_tester_1 = require("./angular2-tester");
describe("Angular2Tester", function () {
    testing_1.TestBed.initTestEnvironment(testing_2.BrowserDynamicTestingModule, testing_2.platformBrowserDynamicTesting());
    var fixture;
    beforeEach(function () {
        testing_1.TestBed.configureTestingModule({
            declarations: [angular2_tester_1.Angular2Tester]
        });
        testing_1.TestBed.compileComponents();
        fixture = testing_1.TestBed.createComponent(angular2_tester_1.Angular2Tester);
    });
    it("should display original title", function () {
        var debugElement = fixture.debugElement.query(platform_browser_1.By.css("h1"));
        fixture.detectChanges();
        expect(debugElement.nativeElement.textContent).toEqual("Hello :)");
    });
    it("should display a different test title", function () {
        var debugElement = fixture.debugElement.query(platform_browser_1.By.css("h1"));
        fixture.componentInstance.title = "Test Title";
        fixture.detectChanges();
        expect(debugElement.nativeElement.textContent).toEqual("Test Title");
    });
});
