"use strict";
var sinon = require("sinon");
var sinon_tester_1 = require("./sinon-tester");
describe("SinonTester", function () {
    var tester = new sinon_tester_1.SinonTester();
    it("should be able to spy", function () {
        var spy = sinon.spy(tester, "add");
        var result = tester.add(2, 2);
        expect(spy.calledWith(2, 2)).toBeTruthy();
        expect(result).toEqual(4);
    });
});
