"use strict";
var index_import_tester_1 = require("./index-import-tester");
var index_1 = require("./dependency/index");
var index_2 = require("./dependency/index");
describe("IndexImporter", function () {
    var tester = new index_import_tester_1.IndexImportTester();
    it("should import using index.ts", function () {
        expect(tester.testImportIndex()).toEqual(new index_1.IndexComponent());
    });
    it("should import using index.ts with a trailing slash", function () {
        expect(tester.testImportIndexWithSlash()).toEqual(new index_2.IndexComponentWithSlash());
    });
});
