"use strict";
var json_import_tester_1 = require("./json-import-tester");
describe("JsonImporter", function () {
    var tester = new json_import_tester_1.JsonImportTester();
    it("should require a local json file", function () {
        expect(tester.testLocalJSON()).toEqual([1, 2, 3, "a", "b", "c"]);
    });
    it("should require a package.json file from a node_modules module", function () {
        expect(tester.testPackageJSON()).not.toBeUndefined();
    });
    it("should require a node_modules module that requires JSON", function () {
        expect(tester.testRequireModuleRequiringJSON()).not.toBeUndefined();
    });
});
