"use strict";
var req = require("./node-require-tester");
describe("NodeRequireTester", function () {
    var tester = new req.NodeRequireTester();
    it("should require a local class", function () {
        var dependency = require("./dependency");
        expect(tester.testRequireLocalClass()).toEqual(new dependency.DependencyComponent());
    });
    it("should require a package from node_modules", function () {
        expect(tester.testRequirePackage()).toEqual(jasmine.any(Function));
    });
    it("should require a package from node_modules with dynamically required dependencies", function () {
        expect(tester.testDynamicDependency()).toEqual(jasmine.any(Function));
    });
    it("should require a text file on a relative path", function () {
        expect(tester.testRequireRelativeTextFile().indexOf("margin: 0;")).toBeGreaterThan(0);
    });
});
