"use strict";
var component_1 = require("./component");
describe("IndexRequireComponent", function () {
    it("should import using index.ts", function () {
        var indexRequireComponent = new component_1.IndexRequireComponent();
        expect(indexRequireComponent.getMessageFromDependency()).toEqual("I'm in a file named index.ts");
    });
    it("should import using index.ts with a trailing slash", function () {
        var indexRequireComponent = new component_1.IndexRequireComponent();
        expect(indexRequireComponent.getMessageFromDependencyWithSlash()).toEqual("I was required using a trailing slash");
    });
});
