"use strict";
var globals_tester_1 = require("./globals-tester");
describe("GlobalsTester", function () {
    var tester = new globals_tester_1.GlobalsTester();
    it("should return Buffer", function () {
        expect(tester.testBuffer()).toEqual(new Buffer("hello"));
    });
    it("should return __filename", function () {
        expect(tester.testFilename()).toContain("/src/node/globals/globals-tester.ts");
    });
    it("should return __dirname", function () {
        expect(tester.testDirname()).toContain("/src/node/globals");
    });
    it("should return global", function () {
        expect(tester.testGlobal()).toEqual(global);
    });
    it("should test process", function () {
        expect(tester.testProcess()).toEqual("/");
    });
});
