"use strict";
var sinon = require("sinon");
var component_1 = require("./component");
describe("CalculatorComponent", function () {
    it("should add numbers", function () {
        var calculatorComponent = new component_1.CalculatorComponent();
        var spy = sinon.spy(calculatorComponent, "add");
        var result = calculatorComponent.add(2, 2);
        expect(spy.calledWith(2, 2)).toBeTruthy();
        expect(result).toEqual(4);
    });
});
